//props são readonly
//Os dados fluem sempre de cima para baixo, ou seja, sempre do pai para o filho.
function Cabecalho(props) {
    return (
        <header>
            <h1>{props.titulo}</h1>
            <h2>6º Período Eng Software</h2>
            <button onClick={props.cliqueBotao} >Invocando o pai</button>

            <img src="%PUBLIC_URL%/logo.svg" width="180px" height="40px" ></img>
            <form>
            <div class ="barradepesquisa" id="barradepesquisa">
            <input type ="text" name="pesquisar" placeholder="O que está procurando ? "></input>
            <a> <i class ="fas fa-search"></i></a>
            </div>
            </form>

            <nav>
            <ul>
            <li><a href="#minhaconta">Minha conta&nbsp; <i class ="fas fa-user-circle"></i></a></li>
            <li>&nbsp; &nbsp; <a href="#loja"><i class ="fas fa-shopping-cart"> &nbsp; |&nbsp; </i></a></li>
            <li><img src="{{url_for('static', filename = 'img/brasil.png')}}" width="30px" height="20px" alt="br"></img></li>&nbsp;
            <li><img src="{{url_for('static', filename = 'img/espanha.png')}}" width="30px" height="20px" alt="es"></img></li>&nbsp;
            <li><img src="{{url_for('static', filename = 'img/inglaterra.png')}}" width="30px" height="20px" alt="en"></img></li>

            </ul>
            </nav>
        </header>
    );
}

